import React, {useState} from 'react';
const Contact = ()=>{
  const [s,setS] = useState({name:'',email:'',msg:''});
  const send = (e)=>{ e.preventDefault(); const mail = `mailto:trident@gmail.com?subject=Contact%20from%20Website&body=Name:%20${encodeURIComponent(s.name)}%0AEmail:%20${encodeURIComponent(s.email)}%0AMessage:%20${encodeURIComponent(s.msg)}`; window.location.href=mail; }
  return (
    <section id="contact" className="section fade-up">
      <div className="grid md:grid-cols-2 gap-6">
        <div className="card">
          <h4 className="font-semibold text-white mb-2">Let's talk</h4>
          <p className="text-gray-300 mb-2">Email: <a href="mailto:trident@gmail.com" className="text-cyan-300">trident@gmail.com</a></p>
          <p className="text-gray-300 mb-2">Phone: <a href="tel:+919112146583" className="text-cyan-300">+91 9112146583</a></p>
          <p className="text-gray-300">Based in Bengaluru â€” focused on helping small businesses across India.</p>
        </div>
        <form className="card" onSubmit={send}>
          <input required value={s.name} onChange={e=>setS({...s,name:e.target.value})} placeholder="Name" className="p-3 rounded border bg-transparent mb-3"/>
          <input required value={s.email} onChange={e=>setS({...s,email:e.target.value})} placeholder="Email" className="p-3 rounded border bg-transparent mb-3"/>
          <textarea required value={s.msg} onChange={e=>setS({...s,msg:e.target.value})} placeholder="Message" className="p-3 rounded border bg-transparent mb-3"></textarea>
          <button className="px-4 py-2 bg-gradient-to-r from-cyan-400 to-violet-600 text-black rounded-full">Send Message</button>
        </form>
      </div>
    </section>
  );
}
export default Contact;
