import React, {useState, useRef, useEffect} from 'react';
const roles = [
  {title:'Internship - Data Analyst', bullets:['Analyze shop sales data','Work with Python & SQL','Build simple dashboards'], stipend:'Paid/Unpaid - Discuss'}
];
const Careers = ()=>{
  const [selected,setSelected] = useState(null);
  const [form,setForm] = useState({name:'',email:'',resume:'',message:''});
  const formRef = useRef(null);

  useEffect(()=>{
    if(selected && formRef.current){
      formRef.current.scrollIntoView({behavior:'smooth', block:'center'});
    }
  },[selected]);

  const apply = (role)=>{
    setSelected(role);
  }

  const submit = (e)=>{
    e.preventDefault();
    const mail = `mailto:trident@gmail.com?subject=Application%20for%20${encodeURIComponent(selected)}&body=Name:%20${encodeURIComponent(form.name)}%0AEmail:%20${encodeURIComponent(form.email)}%0AResume:%20${encodeURIComponent(form.resume)}%0AMessage:%20${encodeURIComponent(form.message)}`;
    window.location.href = mail;
  }

  return (
    <section id="careers" className="section fade-up">
      <h3 className="text-2xl font-bold mb-4">Careers & Internships</h3>
      <p className="text-gray-300 mb-6">Join us â€” internships available for data analysts and front-end developers. Learn on the job and build real products for small businesses.</p>
      <div className="grid md:grid-cols-2 gap-6 mb-6">
        {roles.map((r,i)=>(
          <div key={i} className="card">
            <h4 className="font-semibold text-white">{r.title}</h4>
            <ul className="text-gray-300 list-disc list-inside mt-2">
              {r.bullets.map((b,bi)=>(<li key={bi}>{b}</li>))}
            </ul>
            <div className="mt-4 flex justify-between items-center">
              <div className="text-sm text-gray-300">{r.stipend}</div>
              <button onClick={()=>apply(r.title)} className="px-4 py-2 bg-gradient-to-r from-cyan-400 to-violet-600 text-black rounded-full">Apply</button>
            </div>
          </div>
        ))}
      </div>

      {selected && (
        <div ref={formRef} className="card max-w-2xl mx-auto">
          <h4 className="font-semibold mb-3">Apply for {selected}</h4>
          <form onSubmit={submit} className="flex flex-col gap-3">
            <input required value={form.name} onChange={e=>setForm({...form, name:e.target.value})} placeholder="Name" className="p-3 rounded border bg-transparent"/>
            <input required value={form.email} onChange={e=>setForm({...form, email:e.target.value})} placeholder="Email" className="p-3 rounded border bg-transparent"/>
            <input value={form.resume} onChange={e=>setForm({...form, resume:e.target.value})} placeholder="Resume link (Drive/GitHub)" className="p-3 rounded border bg-transparent"/>
            <textarea value={form.message} onChange={e=>setForm({...form, message:e.target.value})} placeholder="Why do you want to join?" className="p-3 rounded border bg-transparent"></textarea>
            <div className="flex gap-3">
              <button className="px-4 py-2 bg-indigo-600 rounded text-white">Send Application</button>
              <button type="button" onClick={()=>{ setSelected(null); setForm({name:'',email:'',resume:'',message:''}); }} className="px-4 py-2 border rounded">Cancel</button>
            </div>
          </form>
        </div>
      )}
    </section>
  );
}
export default Careers;
